mapper.update:
update

 hall set status = 1 where hall_no =#{num}
 studio set status = 1 where studio_no =#{num}
 dress set status = 1 where dress_no =#{num}
 makeup set status = 1 where makeup_no =#{num}

update

 hall set status = 2 where hall_no =#{num}
 studio set status = 2 where studio_no =#{num}
 dress set status = 2 where dress_no =#{num}
 makeup set status = 2 where makeup_no =#{num}

mapper.select:
select comNo , id ,code , name , tel,addr,status from( select rownum as rnum ,a.* from( select dress_no as comNo, member_id as id, code,dress_name as name,dress_tel as tel,dress_addr as addr,status from dress where status = 0 union all select studio_no,member_id , code,studio_name,studio_tel,studio_addr,status from studio where status = 0 union all select hall_no,member_id , code, hall_name, hall_tel, hall_addr, status from hall where status = 0 union all select makeup_no,member_id , code,makeup_name,makeup_tel,makeup_addr,status from makeup where status = 0 )a
where name like '%'||#{keyword}||'%'
where id like '%'||#{keyword}||'%'
) where rnum between ${start} and ${end}

mapper.update:
update
 hall set viewstatus = 0 where hall_no =#{num}
 studio set viewstatus = 0 where studio_no =#{num}
 dress set viewstatus = 0 where dress_no =#{num}
 makeup set viewstatus = 0 where makeup_no =#{num}

update
 hall set viewstatus = 1 where hall_no =#{num}
 studio set viewstatus = 1 where studio_no =#{num}
 dress set viewstatus = 1 where dress_no =#{num}
 makeup set viewstatus = 1 where makeup_no =#{num}

mapper.select:
select comNo , id ,code , name , tel,addr,status,viewstatus from( select rownum as rnum ,a.* from( select dress_no as comNo, member_id as id, code,dress_name as name,dress_tel as tel,dress_addr as addr,status,viewstatus from dress where viewstatus = 2 or viewstatus =3 union all select studio_no,member_id , code,studio_name,studio_tel,studio_addr,status,viewstatus from studio where viewstatus = 2 or viewstatus =3 union all select hall_no,member_id , code, hall_name, hall_tel, hall_addr, status,viewstatus from hall where viewstatus = 2 or viewstatus =3 union all select makeup_no,member_id , code,makeup_name,makeup_tel,makeup_addr,status,viewstatus from makeup where viewstatus = 2 or viewstatus =3 )a
where name like '%'||#{keyword}||'%'
where id like '%'||#{keyword}||'%'
) where rnum between ${start} and ${end}

mapper.update:
update goods
	 set goods_type = #{goodsType} , goods_name=#{goodsName} , goods_price = #{goodsPrice},goods_tag = #{goodsTag},
	 goods_content = #{goodsContent} , 	viewstatus = #{viewstatus} , goods_filename= #{goodsFileName} , goods_filepath=#{goodsFilePath} where goods_No = #{goodsNo}
