mapper.select:
select hall_no as hallNo, member_id as memberId, code as code, hall_type as hallType, hall_name as hallName, hall_loc as hallLoc, hall_addr as hallAddr, hall_content as hallContent, hall_img as hallImg, hall_path as hallPath, hall_price as hallPrice, hall_min_person as hallMinPerson, hall_max_person as hallMaxPerson, hall_scope as hallScope, hall_foodtype as hallFoodetype, hall_foodMenu as hallFoodmenu, hall_foodprice as hallFoodprice, hall_tel as hallTel, hall_time as hallTime, hall_service_food as hallServiceFood, hall_service_drink as hallServiceDrink, hall_service_parking as hallServiceParking, status as status, viewstatus as viewStatus, hall_tag as hallTag, hall_latitude as hallLatitude, hall_longitude as hallLongitude, hall_date as hallDate from hall
hall_Loc like #{halLoc}
AND hall_Type like #{hallType}
AND hall_Foodtype like #{hallFoodType}
AND hall_Min_Person like #{hallMinPerson}
AND hall_Max_Person like #{hallMaxPerson}
AND hall_Name like #{hallName}
